import React, { useState } from 'react';
import './App.css'; // Import the stylesheet

const App = () => {
  const [isListView, setIsListView] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  const books = [
    { name: 'Book 1', publicationDate: '2023-01-15', description: 'Description of Book 1', status: 'Available' },
    { name: 'Book 2', publicationDate: '2022-11-20', description: 'Description of Book 2', status: 'Not Available' },
    { name: 'Book 3', publicationDate: '2024-03-10', description: 'Description of Book 3', status: 'Available' },
    { name: 'Book 4', publicationDate: '2023-09-05', description: 'Description of Book 4', status: 'Not Available' },
  ];

  const filteredBooks = books.filter(
    book => book.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            book.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mx-auto px-8 py-12 sm:py-16 lg:py-20"> {/* Adjust margin and padding */}
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Books List</h1>
        <button
          onClick={() => setIsListView(!isListView)}
          className="btn-switch absolute top-0 right-0 mt-4 mr-4"         >
          Switch to {isListView ? 'Grid View' : 'List View'}
        </button>
      </div>
      <input
        type="text"
        placeholder="Search..."
        onChange={e => setSearchTerm(e.target.value)}
        className="input" // Apply a custom class for the input field
      />
      {isListView ? (
        <table className="table"> {/* Apply a custom class for the table */}
          <thead className="table-header"> {/* Apply a custom class for the table header */}
            <tr>
              <th className="table-header-cell">Book Name</th> {/* Apply a custom class for the table header cell */}
              <th className="table-header-cell">Publication Date</th> {/* Apply a custom class for the table header cell */}
              <th className="table-header-cell">Description</th> {/* Apply a custom class for the table header cell */}
              <th className="table-header-cell">Status</th> {/* Apply a custom class for the table header cell */}
            </tr>
          </thead>
          <tbody>
            {filteredBooks.map((book, index) => (
              <tr key={index} className={index % 2 === 0 ? 'even-row' : 'odd-row'}> {/* Apply classes for alternating row colors */}
                <td className="table-cell">{book.name}</td> {/* Apply a custom class for the table cell */}
                <td className="table-cell">{book.publicationDate}</td> {/* Apply a custom class for the table cell */}
                <td className="table-cell">{book.description}</td> {/* Apply a custom class for the table cell */}
                <td className="table-cell">{book.status}</td> {/* Apply a custom class for the table cell */}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <div className="grid-container"> {/* Apply a custom class for the grid container */}
          {filteredBooks.map((book, index) => (
            <div key={index} className="grid-item"> {/* Apply a custom class for the grid item */}
              <div className="name font-bold mb-2">{book.name}</div> {/* Apply a custom class for the name */}
              <div>{book.publicationDate}</div>
              <div>{book.description}</div>
              <div>{book.status}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
