import React, { useState } from 'react';
import './App.css'; // Import the stylesheet

const App = () => {
  const [isListView, setIsListView] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  const users = [
    { name: 'Javaid Lateef', cnic: '35202-88888888-8' },
    { name: 'Kamran Arshad', cnic: '35202-48930280-9' },
    { name: 'Maimoona Khalid', cnic: '35202-432443243-0' },
    { name: 'Shahita Khan', cnic: '35202-4324324324-0' },
  ];

  const filteredUsers = users.filter(
    user => user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            user.cnic.includes(searchTerm)
  );

  return (
    <div className="container mx-auto px-8 py-12 sm:py-16 lg:py-20"> {/* Adjust margin and padding */}
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Books List</h1>
        <button
          onClick={() => setIsListView(!isListView)}
          className="btn-switch absolute top-0 right-0 mt-4 mr-4"         >
          Switch to {isListView ? 'Grid View' : 'List View'}
        </button>
      </div>
      <input
        type="text"
        placeholder="Search..."
        onChange={e => setSearchTerm(e.target.value)}
        className="input" // Apply a custom class for the input field
      />
      {isListView ? (
        <table className="table"> {/* Apply a custom class for the table */}
          <thead className="table-header"> {/* Apply a custom class for the table header */}
            <tr>
              <th className="table-header-cell">Name</th> {/* Apply a custom class for the table header cell */}
              <th className="table-header-cell">CNIC</th> {/* Apply a custom class for the table header cell */}
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map((user, index) => (
              <tr key={index} className={index % 2 === 0 ? 'even-row' : 'odd-row'}> {/* Apply classes for alternating row colors */}
                <td className="table-cell">{user.name}</td> {/* Apply a custom class for the table cell */}
                <td className="table-cell">{user.cnic}</td> {/* Apply a custom class for the table cell */}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <div className="grid-container"> {/* Apply a custom class for the grid container */}
          {filteredUsers.map((user, index) => (
            <div key={index} className="grid-item"> {/* Apply a custom class for the grid item */}
              <div className="name font-bold mb-2">{user.name}</div> {/* Apply a custom class for the name */}
              <div>{user.cnic}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
