import React, { useState } from 'react';

const tableHeaderStyle = 'bg-gray-200';
const gridItemStyle = 'p-4 border-2 border-gray-300 rounded bg-white shadow-md';

const commonTableStyles = 'rounded-lg font-montserrat text-center border-collapse w-full';
const commonGridViewStyles = 'grid gap-8 grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4';

/*type UserData = {
  name: string;
  cnic: string;
};*/

const users = [
  { name: 'Javaid Lateef', cnic: '35202-88888888-8' },
  { name: 'Kamran Arshad', cnic: '35202-48930280-9' },
  { name: 'Maimoona Khalid', cnic: '35202-432443243-0' },
  { name: 'Shahita Khan', cnic: '35202-4324324324-0' },
];

const App = () => {
  const [isListView, setIsListView] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  const filteredUsers = users.filter(
    user => user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            user.cnic.includes(searchTerm)
  );

  return (
    <div className="m-8 sm:m-12 lg:m-20">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">User Data</h1>
        <button
          onClick={() => setIsListView(!isListView)}
          className="py-2 px-4 rounded bg-blue-500 text-white hover:bg-blue-600 cursor-pointer"
        >
          Switch to {isListView ? 'Grid View' : 'List View'}
        </button>
      </div>
      <input
        type="text"
        placeholder="Search..."
        onChange={e => setSearchTerm(e.target.value)}
        className="p-2 rounded w-full mb-8 border border-gray-300"
      />
      {isListView ? (
        <table className={`${commonTableStyles} table-auto`}>
          <thead className={tableHeaderStyle}>
            <tr>
              <th className="py-2 px-4">Name</th>
              <th className="py-2 px-4">CNIC</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map((user, index) => (
              <tr key={index} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-100'}>
                <td className="py-2 px-4">{user.name}</td>
                <td className="py-2 px-4">{user.cnic}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <div className={commonGridViewStyles}>
          {filteredUsers.map((user, index) => (
            <div key={index} className={gridItemStyle}>
              <div className="font-bold mb-2">{user.name}</div>
              <div>{user.cnic}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
export default App;
