import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [isListView, setIsListView] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  const books = [
    { name: 'The Catcher in the Rye', publicationDate: '1951-07-16', description: 'A novel by J.D. Salinger', status: 'Available' },
    { name: 'To Kill a Mockingbird', publicationDate: '1960-07-11', description: 'A novel by Harper Lee', status: 'Not Available' },
    { name: 'The Hobbit', publicationDate: '1937-09-21', description: 'A fantasy novel by J.R.R. Tolkien', status: 'Available' },
    { name: 'Harry Potter and the Philosopher\'s Stone', publicationDate: '1997-06-26', description: 'A fantasy novel by J.K. Rowling', status: 'Not Available' },
  ];


  const filteredBooks = books.filter(
    book => book.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            book.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mx-auto px-8 py-12 sm:py-16 lg:py-20"> 
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Books List</h1>
        <button
          onClick={() => setIsListView(!isListView)}
          className="btn-switch absolute top-0 right-0 mt-4 mr-4"         >
          Switch to {isListView ? 'Grid View' : 'List View'}
        </button>
      </div>
      <input
        type="text"
        placeholder="Search..."
        onChange={e => setSearchTerm(e.target.value)}
        className="input" 
      />
      {isListView ? (
        <table className="table"> 
          <thead className="table-header"> 
            <tr>
              <th className="table-header-cell">Book Name</th> 
              <th className="table-header-cell">Publication Date</th> 
              <th className="table-header-cell">Description</th> 
              <th className="table-header-cell">Status</th>
            </tr>
          </thead>
          <tbody>
            {filteredBooks.map((book, index) => (
              <tr key={index} className={index % 2 === 0 ? 'even-row' : 'odd-row'}> 
                <td className="table-cell">{book.name}</td> 
                <td className="table-cell">{book.publicationDate}</td> 
                <td className="table-cell">{book.description}</td> 
                <td className="table-cell">{book.status}</td> 
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <div className="grid-container"> 
          {filteredBooks.map((book, index) => (
            <div key={index} className="grid-item"> 
              <div className="name font-bold mb-2">{book.name}</div> 
              <div>{book.publicationDate}</div>
              <div>{book.description}</div>
              <div>{book.status}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
